class Particle{constructor(){this.x=random(0,width),this.y=random(0,height),this.r=random(1,8),this.xSpeed=random(-1.3,1.3),this.ySpeed=random(-0.5,1.)}createParticle(){noStroke(),fill("rgba(125,125,125,0.5)"),circle(this.x,this.y,this.r)}moveParticle(){(this.x<0||this.x>width)&&(this.xSpeed*=-1),(this.y<0||this.y>height)&&(this.ySpeed*=-1),this.x+=this.xSpeed,this.y+=this.ySpeed}joinParticles(t){t.forEach(t=>{dist(this.x,this.y,t.x,t.y)<85&&(stroke("rgba(125,125,125,0.2)"),line(this.x,this.y,t.x,t.y))})}}let particles=[];function setup(){createCanvas(windowWidth,windowHeight).class("backgroundsketch");for(let t=0;t<width/10;t++)particles.push(new Particle)}function draw(){background("#cfdee3");for(let t=0;t<particles.length;t++)particles[t].createParticle(),particles[t].moveParticle(),particles[t].joinParticles(particles.slice(t))}
